    <div ref="articlesBlock" class="articles">
<!--      <div class="">-->
<!--        <h2 class="text">This is some text</h2>-->
<!--        <h2 class="typing_text-heading">-->
<!--          <span class="typing_text"></span><span class="cursor">_</span>-->
<!--        </h2>-->
