      <div ref="articlesPosts" v-for="article in articles" class="article">
        <template v-if="article?.colors">
          <h2>{{ article.title }}</h2>
          <div class="dress-code-desc">Будем признательны, если вы поддержите цветовую гамму нашей свадьбы</div>
          <div class="dress-code-list">
            <div v-for="color in article.colors" :style="{ 'border-color': color.color }" class="dress-code">
              <div class="dress-code-body">
                <span :style="{ 'color': color.color }">{{ color.title }}</span>
              </div>
              <div :style="{ 'background-color': color.color }" class="dress-code-circle"></div>
            </div>
          </div>
        </template>
        <template v-else-if="article?.times">
          <h2>{{ article.title }}</h2>
          <div class="schedule-list">
            <div v-for="time in article.times" class="schedule">
              <div class="schedule-time">
                {{ time.time }}
              </div>
              <svg width="33" height="32" viewBox="0 0 33 32" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M16.5 1.33325L12.5 11.9999L1.83331 15.9999L12.5 19.9999L16.5 30.6666L20.5 19.9999L31.1666 15.9999L20.5 11.9999L16.5 1.33325Z" fill="#C9C9C9"/>
              </svg>
              <div class="">
                <div class="schedule-title">
                  {{ time.title }}
                </div>
                <div class="schedule-description">
                  {{ time.description }}
                </div>
              </div>
            </div>
          </div>
        </template>
        <template v-else>
          <h2>{{ article.title }}</h2>
          <p>{{ article.desc }}</p>
        </template>
      </div>
    </div>
